import { useState } from "react"


export default function Temperature(props){
    const[unit,setUnit]=useState("cel")
    
    
    if (unit==="cel"){
    
    return(
   <span>
    <span className="temperature"> {Math.round(props.temp)}</span> 
    <span className="units">C° </span>
    </span>
    )}
    else{
        return(
        <span>
    <span className="temperature"> {Math.round(props.temp)}</span> 
    <span className="units">C° </span>
    </span>
    )



    }


}
