import axios from "axios"
import { useState } from "react"
import "./WeatherForecast.css"
import ForecastDay from "./ForecastDay"

export default function WeatherForecast(props){
    const [ready,setReady]=useState(false)
    const [forecast,setforecast]=useState()
    let iconUrl=`https://openweathermap.org/img/wn/${forecast[0].weather[0].icon}@2x.png`;
    function response (response){
        setReady(true)
        setforecast(response.data.daily)
    }
   

    if(ready){
    return (
        <div className="WeatherForecast">
            <div className="row"><div className="col"> 
            <div className="WeatherForecastDay"><ForecastDay date={props.weatherData.date}/></div>  <img className="WeatherForecastIMG" src={iconUrl} alt={props.weatherData.description}></img> <div className="WeatherForecastTemp"> <span className="maxTemp">{Math.round(forecast[0].temp.max)}°</span> <span className="minTemp">{Math.round(forecast[0].temp.min)}°</span></div></div> </div> </div>
)}else{

    let lon=props.weatherData.cordinates.lon
    let lat=props.weatherData.cordinates.lat
    let apiUrl=`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=b0ce35cd54f47a37e37cfe580baaccdd&units=metric`
    axios.get(apiUrl).then(response)
return null
}



}
